// src/pages/AnaliseDeDados.tsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

interface Acao {
    acao: string;
    preco_atual: number;
    crescimento_percentual: number;
    erro?: string;
}

const AnaliseDeDados: React.FC = () => {
    const [acoes, setAcoes] = useState<Acao[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchAcoes = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:5000/analise_acoes');
                setAcoes(response.data.acoes || []); // Garanta que 'acoes' é um array
            } catch (error) {
                console.error('Erro ao buscar dados da API:', error);
                setError('Erro ao buscar dados da API');
            } finally {
                setLoading(false);
            }
        };

        fetchAcoes();
    }, []);

    if (loading) {
        return <div>Carregando...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h1>Análise de Ações</h1>
            <table>
                <thead>
                    <tr>
                        <th>Ação</th>
                        <th>Preço Atual</th>
                        <th>Crescimento Percentual</th>
                        <th>Erro</th>
                    </tr>
                </thead>
                <tbody>
                    {acoes.map((acao, index) => (
                        <tr key={index}>
                            <td>{acao.acao}</td>
                            <td>{acao.preco_atual !== undefined ? acao.preco_atual.toFixed(2) : '-'}</td>
                            <td>{acao.crescimento_percentual !== undefined ? acao.crescimento_percentual.toFixed(2) : '-'}</td>
                            <td>{acao.erro || '-'}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AnaliseDeDados;
