import React, { useState } from 'react';
import './Financeiro.css'; // Importa o CSS

const Financeiro: React.FC = () => {
    const [abaAtiva, setAbaAtiva] = useState<'ganhos' | 'gastos' | 'total'>('ganhos');
    const [abaNumeroAtiva, setAbaNumeroAtiva] = useState<number>(1);

    const renderConteudo = () => {
        switch (abaAtiva) {
            case 'ganhos':
                return <div>Ganhos: Conteúdo da aba {abaNumeroAtiva}</div>;
            case 'gastos':
                return <div>Gastos: Conteúdo da aba {abaNumeroAtiva}</div>;
            case 'total':
                return <div>Total Somado: Conteúdo da aba {abaNumeroAtiva}</div>;
            default:
                return null;
        }
    };

    return (
        <div className="financeiro-container">
            <h1>Financeiro</h1>
            <div className="abas">
                <button onClick={() => setAbaAtiva('ganhos')} className={abaAtiva === 'ganhos' ? 'ativa' : ''}>Ganhos</button>
                <button onClick={() => setAbaAtiva('gastos')} className={abaAtiva === 'gastos' ? 'ativa' : ''}>Gastos</button>
                <button onClick={() => setAbaAtiva('total')} className={abaAtiva === 'total' ? 'ativa' : ''}>Total Somado</button>
            </div>
            <div className="abas-numeradas">
                {[1, 2, 3].map(numero => (
                    <button
                        key={numero}
                        onClick={() => setAbaNumeroAtiva(numero)}
                        className={abaNumeroAtiva === numero ? 'ativa' : ''}
                    >
                        {numero}
                    </button>
                ))}
            </div>
            <div className="conteudo">
                {renderConteudo()}
            </div>
        </div>
    );
};

export default Financeiro;
