import React, { useState } from 'react';
import './Financeiro.css'; // Importa o CSS

const Financeiro: React.FC = () => {
    const [abaAtiva, setAbaAtiva] = useState<'ganhos' | 'gastos' | 'total'>('ganhos');

    const renderConteudo = () => {
        switch (abaAtiva) {
            case 'ganhos':
                return <div>Ganhos: Aqui você pode listar seus ganhos.</div>;
            case 'gastos':
                return <div>Gastos: Aqui você pode listar seus gastos.</div>;
            case 'total':
                return <div>Total Somado: Aqui você pode ver o total de ganhos e gastos.</div>;
            default:
                return null;
        }
    };

    return (
        <div className="financeiro-container">
            <h1>Financeiro</h1>
            <div className="abas">
                <button onClick={() => setAbaAtiva('ganhos')} className={abaAtiva === 'ganhos' ? 'ativa' : ''}>Ganhos</button>
                <button onClick={() => setAbaAtiva('gastos')} className={abaAtiva === 'gastos' ? 'ativa' : ''}>Gastos</button>
                <button onClick={() => setAbaAtiva('total')} className={abaAtiva === 'total' ? 'ativa' : ''}>Total Somado</button>
            </div>
            <div className="conteudo">
                {renderConteudo()}
            </div>
        </div>
    );
};

export default Financeiro;
