import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CotacoesAtuais: React.FC = () => {
    const [acoes, setAcoes] = useState<any[]>([]);
    const [criptos, setCriptos] = useState<any[]>([]);

    const alphaVantageApiKey = '219FI7X7E7DINFXH'; // Substitua pela sua chave API do Alpha Vantage

    // Símbolos das 5 ações mais populares
    const symbols = ['AAPL', 'MSFT', 'GOOGL', 'AMZN', 'TSLA'];

    // IDs das 5 criptomoedas mais populares
    const cryptoIds = ['bitcoin', 'ethereum', 'binancecoin', 'ripple', 'dogecoin'];

    useEffect(() => {
        // Função para buscar cotações de ações
        const fetchAcoes = async () => {
            const promises = symbols.map(symbol =>
                axios.get(`https://www.alphavantage.co/query?function=TIME_SERIES_INTRADAY&symbol=${symbol}&interval=5min&apikey=${alphaVantageApiKey}`)
            );
            const results = await Promise.all(promises);
            const acoesData = results.map(result => {
                const timeSeries = result.data['Time Series (5min)'];
                const latestTimestamp = Object.keys(timeSeries)[0];
                return {
                    symbol: result.data['Meta Data']['2. Symbol'],
                    price: timeSeries[latestTimestamp]['1. open']
                };
            });
            setAcoes(acoesData);
        };

        // Função para buscar cotações de criptomoedas
        const fetchCriptos = async () => {
            const result = await axios.get(`https://api.coingecko.com/api/v3/simple/price?ids=${cryptoIds.join(',')}&vs_currencies=usd`);
            const criptosData = Object.keys(result.data).map(id => ({
                name: id.charAt(0).toUpperCase() + id.slice(1),
                price: result.data[id].usd
            }));
            setCriptos(criptosData);
        };

        fetchAcoes();
        fetchCriptos();

        // Atualiza as cotações a cada 5 minutos
        const interval = setInterval(() => {
            fetchAcoes();
            fetchCriptos();
        }, 300000);

        return () => clearInterval(interval);

    }, []);

    return (
        <div style={{ padding: '20px', textAlign: 'center' }}>
            <h1>Cotações Atuais</h1>
            <div style={{ marginTop: '20px' }}>
                <h2>Ações</h2>
                <ul>
                    {acoes.map((acao, index) => (
                        <li key={index}>
                            {acao.symbol}: ${acao.price}
                        </li>
                    ))}
                </ul>
            </div>
            <div style={{ marginTop: '20px' }}>
                <h2>Criptomoedas</h2>
                <ul>
                    {criptos.map((cripto, index) => (
                        <li key={index}>
                            {cripto.name}: ${cripto.price}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default CotacoesAtuais;
