import React, { useState } from 'react';
import './ListaDeTarefas.css';

type Tarefa = {
    id: number;
    texto: string;
    concluida: boolean;
};

const ListaDeTarefas: React.FC = () => {
    const [tarefas, setTarefas] = useState<Tarefa[]>([
        { id: 1, texto: 'Estudar TypeScript', concluida: false },
        { id: 2, texto: 'Desenvolver aplicação React', concluida: false },
        { id: 3, texto: 'Revisar código', concluida: false },
    ]);
    const [novoTexto, setNovoTexto] = useState<string>('');

    const toggleConcluida = (id: number) => {
        setTarefas(
            tarefas.map(tarefa =>
                tarefa.id === id ? { ...tarefa, concluida: !tarefa.concluida } : tarefa
            )
        );
    };

    const adicionarTarefa = () => {
        if (novoTexto.trim()) {
            const novaTarefa = {
                id: Date.now(),
                texto: novoTexto,
                concluida: false,
            };
            setTarefas([...tarefas, novaTarefa]);
            setNovoTexto(''); // Limpa o campo de entrada
        }
    };

    const excluirTarefa = (id: number) => {
        setTarefas(tarefas.filter(tarefa => tarefa.id !== id));
    };

    return (
        <div>
            <h1>Lista de Tarefas</h1>
            <div>
                <input
                    type="text"
                    value={novoTexto}
                    onChange={(e) => setNovoTexto(e.target.value)}
                    placeholder="Digite uma nova tarefa"
                />
                <button onClick={adicionarTarefa}>Adicionar</button>
            </div>
            <ul>
                {tarefas.map(tarefa => (
                    <li
                        key={tarefa.id}
                        style={{
                            textDecoration: tarefa.concluida ? 'line-through' : 'none',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'space-between',
                            cursor: 'pointer',
                        }}
                    >
                        <span onClick={() => toggleConcluida(tarefa.id)}>
                            {tarefa.texto}
                        </span>
                        <button onClick={() => excluirTarefa(tarefa.id)}>
                            Excluir
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ListaDeTarefas;
